import "./App.css";
import Home from "./Components/Home";
import Contact from "./Components/Contact";
import Aboutus from "./Components/Aboutus";
// import BrowserRouter from "react-router-dom";
import { BrowserRouter as Router, Route, Routes, Link } from "react-router-dom";
import { useState } from "react";

function App() {
  const [currentTab, setCurrentTab] = useState("home");

  function renderBtn(newTab) {
    if(currentTab !== newTab) setCurrentTab(newTab);
  }

  return (
    <Router>
      <nav className="mt-20 mb-15">
        <ul className="flex justify-center gap-20 text-4xl font-medium tracking-wide text-green-700">
          <li
            className={`border-3 p-2 ${
              currentTab === "home" ? "-translate-y-4" : ""
            }`}
          >
            <Link to="/">
              Home
            </Link>
          </li>
          <li className="border-3 p-2">
            <Link to="/about" onClick={renderBtn("contact")}>
              About
            </Link>
          </li>
          <li className="border-3 p-2">
            <Link to="/contact" onClick={renderBtn("contact")}>
              Contact
            </Link>
          </li>
        </ul>
      </nav>

      <Routes>
        <Route path="/" element={<Home state={"active"} />}></Route>
        <Route path="/about" element={<Aboutus state={"active"} />}></Route>
        <Route path="/contact" element={<Contact state={"active"} />}></Route>
      </Routes>
    </Router>
  );
}

export default App;
